import dataiku
import inference
from elasticsearch import Elasticsearch

model_folder = dataiku.Folder("UKTXMoc0")
model = inference.model_fn(model_folder.get_path())

es = Elasticsearch()

def api_py_function(query):
    feature_vector = inference.predict_fn(query, model)
    search_query = {
        "size": 10,
        "query": {
            "script_score": {
                "query": {
                    "match_all": {}
                },
                "script": {
                    "source": "cosineSimilarity(params.queryVector, 'zalando_nlu_vector') + 1.0",
                    "params": {
                        "queryVector": feature_vector
                    }
                }
            }
        }
    }
    response = es.search(index='idx_zalando', **search_query)
    results = [r['_source']['image'] for r in response['hits']['hits']]
    return results
